import React from 'react';
import dino from '../../img/dinoLove.svg';
import { Panel, PanelHeader, Button, Group, Div, ConfigProvider, SplitLayout, SplitCol, useAdaptivity, useAdaptivityConditionalRender } from '@vkontakte/vkui';
import './Home.css';

// import {
// 	AdaptivityContext,
// 	AdaptivityProps,
// 	SizeType,
// 	ViewHeight,
// 	ViewWidth,
// } from "./AdaptivityContext";

// import {
// 	useBridgeAdaptivity,
// 	BridgeAdaptivity,
// } from "../../hooks/useBridgeAdaptivity";

const Home = ({ id, fetchedUser, go }) => {

	const DESKTOP_SIZE = 1280;

	const MOBILE_SIZE = 320;



	return (
		<SplitCol width="100%" maxWidth="560px" stretchedOnMobile autoSpaced>
			fhgfhf
			<Panel id={id}>


				<PanelHeader>..и немного о важном</PanelHeader>


				<Group className={'Container'} mode={'card'}>
					<Div className={'Greeting'}>
						<img className={'Dino'} src={dino} />
						{fetchedUser && (

							<Div>
								<p> {`привет, ${fetchedUser.first_name}`}</p>
								<p> сегодня поговорим о важном...</p>
								<p> насколько твои друзья любят котиков</p>
							</Div>
						)}
					</Div>
				</Group>

				<Div className='Button__position' size={'i '} sizeY={'regular'}>
					<Button
						className='Button'
						onClick={go}
						data-to='col'
						// data-to='chooseFriend'///////////////////////////////////////
						appearance={'positive'}
						size={'i'}
						stretched={false}
					>
						Поехали
					</Button>
				</Div>
			</Panel>
		</SplitCol>

	);

}
export default Home;
