    const [fetchedFriend, setFriend] = useState([]);


    const fetchFriends = () => {
        bridge.send('VKWebAppGetFriends')
            .then((data) => {
                if (data.users) {
                    console.log(data.users[0]);
                    setFriend(data.users[0])
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }

