import './App.css'
import '@vkontakte/vkui/dist/vkui.css';
import bridge from '@vkontakte/vk-bridge';
import React, { useState, useEffect } from 'react';
import { ChooseFriend } from './components/choosеFriend/СhooseFriend';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol, Div } from '@vkontakte/vkui';
import Home from './components/home/Home';

const App = () => {
	const ROUTES = {
		HOME: 'home',
		CHOOSEFRIEND: 'chooseFriend',
	};

	const [activePanel, setActivePanel] = useState(ROUTES.HOME);
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(<ScreenSpinner size='large' />);
	const [isLoading, setIsLoading] = useState(false);



	useEffect(() => {
		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	return (
		<ConfigProvider>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout}>
						<SplitCol>
							<View activePanel={activePanel}>
								<Home id={ROUTES.HOME} fetchedUser={fetchedUser} go={go} />
								<ChooseFriend id={ROUTES.CHOOSEFRIEND} go={go} activePanel={activePanel} />
							</View>
						</SplitCol>
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}

export default App;
