import bridge from '@vkontakte/vk-bridge';
import { useState } from 'react';
import {
    Panel,
    PanelHeader,
    Header,
    Button,
    Group,
    Cell,
    Div,
    Avatar,
} from '@vkontakte/vkui';
import './MainPage.css';


export function MainPage() {
    const [fetchedFriend, setFriend] = useState([]);

    const fetchFriends = () => {
        bridge
            .send('VKWebAppGetFriends')
            .then((data) => {
                if (data.users) {
                    console.log(data.users[0]);
                    setFriend(data.users[0]);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const name = fetchedFriend.first_name;

    return (
        <>
            <Div>
                <Button
                    onClick={fetchFriends}
                    data-to='home'
                    appearance={'positive'}
                    size={'i'}
                    stretched={false}
                >
                    выбрать друга
                </Button>
            </Div>
            {name === null || name === undefined ? null : (
                <>
                    <p> {`насколько, ${name} любит котиков`}</p>

                    <Div className='Person'>
                        <Group mode={'card'}>
                            <HorizontalCell
                                before={
                                    fetchedFriend.photo_200 ? (
                                        <Avatar src={fetchedFriend.photo_200} />
                                    ) : null
                                }
                            >
                                <p> {name}</p>
                            </HorizontalCell>
                            <Div >

                            </Div>
                        </Group>
                    </Div>
                </>
            )}
        </>
    );
}
