import bridge from '@vkontakte/vk-bridge';
import { useState } from "react";
import { Panel, PanelHeader, Header, Button, Group, Cell, Div } from '@vkontakte/vkui';



export function MainPage() {

    const [fetchedFriend, setFriend] = useState([]);


    const fetchFriends = () => {
        bridge.send('VKWebAppGetFriends')
            .then((data) => {
                if (data.users) {
                    console.log(data.users[0].first_name);
                    setFriend(data.users[0])
                }
            })
            .catch((error) => {
                console.log(error);
            });
    }

    const friend = fetchedFriend.first_name;

    return (

        <>
            <Div  >
                <Button onClick={fetchFriends} data-to="home" appearance={'positive'} size={'i'} stretched={false}>
                    выбрать друга
                </Button>
            </Div>
            {friend === null || friend === undefined ? null :
                <>
                    <p> {`насколько, ${friend} любит котиков`}</p>

                    <Div></Div>
                </>
            }

        </>
    );
}
