import bridge from '@vkontakte/vk-bridge';
import { useState } from 'react';
import {
    Panel,
    PanelHeader,
    Header,
    Button,
    Group,
    Cell,
    Div,
    Avatar, HorizontalCell
} from '@vkontakte/vkui';
import './ChooseFriend.css';
import dino from '../../img/dinoLove.svg';
import { useNavigate } from "react-router-dom";

export function ChooseFriend(id, go, setActivePanel) {

    const [fetchedFriend, setFriend] = useState([]);
    let navigate = useNavigate();
    const fetchFriends = () => {
        bridge
            .send('VKWebAppGetFriends')
            .then((data) => {
                if (data.users) {
                    console.log(data.users[0]);
                    setFriend(data.users[0]);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };



    const successRedirect = () => {
        const name = fetchedFriend.first_name;

        if (name === null || name === undefined) {
            return navigate("/home");
        }


    }

    return (
        <Panel id={id} >

            <Div className='Container'>

                <Div className={'Dino__position'}>
                    <img className={'Dino__img'} src={dino} />
                </Div>


                <Div className='Button__position'>
                    <Button className='Button'
                        onClick={fetchFriends}
                        data-to='home'
                        appearance={'positive'}
                        stretched={false}
                        size={'i '} sizeY={'regular'}
                    >
                        выбрать друга
                    </Button>
                </Div>

                {name === null || name === undefined ? null :
                    setActivePanel('home')
                }





            </Div>

        </Panel>
    );
}
