import bridge from '@vkontakte/vk-bridge';
import { useState, useEffect } from 'react';
import { Panel, Button, Div, Avatar, HorizontalCell } from '@vkontakte/vkui';
import './ChooseFriend.css';
import dino from '../../img/dinoLove.svg';
import { ProgressBar } from '../progressBar/ProgressBar.js';

export function ChooseFriend(id) {
    const [fetchedFriend, setFriend] = useState([]);
    const [isPressed, setIsPressed] = useState(false);
    const [completed, setCompleted] = useState(0);
    const [disabled, setDisabled] = useState(false);
    const [status, setStatus] = useState(['этого придется любить больше', 'это больше не наш друг', 'врет, кажись. на 100% можно любить только картошечку']);

    const loveFriend = 'этого придется любить больше'
    const dontLoveFriend = 'это больше не наш друг'
    const FriendLies = 'врет, кажись. на 100% можно любить только картошечку'


    const fetchFriends = () => {
        setDisabled(false);
        setCompleted(0);

        bridge
            .send('VKWebAppGetFriends')
            .then((data) => {
                if (data.users) {
                    console.log(data.users[0]);
                    setFriend(data.users[0]);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const loader = () => {
        setIsPressed(true);
        setDisabled(true);
        setCompleted(Math.floor(Math.random() * (100 - 1)));
        timer()

    };

    const timer = () => {
        setTimeout(() => {
            loader();
            confirm("nvnv")
            setIsPressed(false);
        }, 1000);

    }

    // useEffect(() => {

    //     isPressed ?
    //         timer = setTimeout(() => {
    //             confirm("nvnv")
    //         }, 1000) : ""


    //     return () => clearTimeout(timer);
    // }, [isPressed]);


    const name = fetchedFriend.first_name;

    return (
        <Panel id={id}>
            <Div className='Container'>
                <Div className='Button__position'>
                    <Button
                        className='Button'
                        onClick={fetchFriends}
                        data-to='home'
                        appearance={'positive'}
                        stretched={false}
                        size={'i '}
                        sizeY={'regular'}
                    >
                        выбрать друга
                    </Button>
                </Div>

                {name === null || name === undefined ? null : (
                    <>
                        <h4> {`насколько, ${name} любит котиков`}</h4>

                        <Div className='Person'>
                            <HorizontalCell size='s' header={name}>
                                <Avatar
                                    size={56}
                                    src={fetchedFriend.photo_200}
                                />
                            </HorizontalCell>
                        </Div>

                        <Div className='Button'>
                            <Button
                                className='Button__size'
                                onClick={loader}
                                appearance={'positive'}
                                size={'i'}
                                stretched={false}
                                disabled={disabled}
                            >
                                узнать
                            </Button>
                        </Div>
                        {<ProgressBar completed={isPressed ? completed : 0} />}

                        {timer}

                    </>
                )}
            </Div>
        </Panel>
    );
}
