export const ProgressBar = (props) => {
    const { completed } = props;

    const Body = {
        height: 40,
        width: '100%',
        backgroundColor: '#e0e0de',
        borderRadius: 50,
        margin: 50,
    };

    const Fill = {
        height: '100%',
        width: `${completed}%`,
        backgroundColor: '#4BB34B',
        borderRadius: 'inherit',
        textAlign: 'right',
        padding: 0,
        transition: 'width 1s ease-in-out',
    };

    const Percent = {
        padding: 10,
        color: 'white',
        fontWeight: 'bold',
        width: 61,
    };

    return (
        <div className={'Container'}>
            <div style={Body}>
                <div style={Fill}>
                    <div style={Percent}>{`${completed}%`}</div>
                </div>
            </div>
        </div>
    );
};
