import './App.css'
import '@vkontakte/vkui/dist/vkui.css';
import bridge from '@vkontakte/vk-bridge';
import React, { useState, useEffect } from 'react';
import { ChooseFriend } from './components/choosеFriend/СhooseFriend';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol, Button } from '@vkontakte/vkui';
import Home from './components/home/Home';
import Colomn from './Colomn';
// const { viewWidth } = useAdaptivityConditionalRender();



const App = () => {

	// const isMobile = platform !== VKCOM;
	// const isDesktop = !isMobile;


	const ROUTES = {
		HOME: 'home',
		CHOOSEFRIEND: 'chooseFriend',
	};

	const STORAGE_KEYS = {
		STATE: 'state',
		STATUS: 'viewStatus',
	};

	const menuWidth = "280px";
	const contentWidth = "660px";




	const [activePanel, setActivePanel] = useState(ROUTES.HOME);
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(<ScreenSpinner size='large' />);
	const [fetchedState, setFetchedState] = useState(null);
	const [userHasSeenIntro, setUserHasSeenIntro] = useState(false);


	useEffect(() => {
		bridge.subscribe(({ detail: { type, data } }) => {
			if (type === 'VKWebAppUpdateConfig') {
				const schemeAttribute = document.createAttribute('scheme');
				schemeAttribute.value = data.scheme ? data.scheme : 'client_light';
				document.body.attributes.setNamedItem(schemeAttribute);
			}
		});
		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			const sheetState = await bridge.send('VKWebAppStorageGet', { keys: [STORAGE_KEYS.STATE, STORAGE_KEYS.STATUS] });
			if (Array.isArray(sheetState.keys)) {
				const data = {};
				sheetState.keys.forEach(({ key, value }) => {
					try {
						data[key] = value ? JSON.parse(value) : {};
						switch (key) {
							case STORAGE_KEYS.STATE:
								setFetchedState(data[STORAGE_KEYS.STATE]);
								break;
							case STORAGE_KEYS.STATUS:
								if (data[key] && data[key].hasSeenIntro) {
									setActivePanel(ROUTES.HOME);
									setUserHasSeenIntro(true);
								}
								break;
							default:
								break;
						}
					} catch (error) {
						setSnackbar(<Snackbar
							layout='vertical'
							onClose={() => setSnackbar(null)}
							before={<Avatar size={24} style={{ backgroundColor: 'var(--dynamic_red)' }}><Icon24Error fill='#fff' width={14} height={14} /></Avatar>}
							duration={900}
						>
							Проблема с получением данных из Storage
						</Snackbar>
						);
						setFetchedState({});
					}
				});
			} else {
				setFetchedState({});
			}
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);


	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	const viewIntro = async (panel) => {
		try {
			await bridge.send('VKWebAppStorageSet', {
				key: STORAGE_KEYS.STATUS,
				value: JSON.stringify({
					hasSeenIntro: true,
				}),
			});
			go(panel);
		} catch (error) {
			setSnackbar(<Snackbar
				layout='vertical'
				onClose={() => setSnackbar(null)}
				before={<Avatar size={24} style={{ backgroundColor: 'var(--dynamic_red)' }}><Icon24Error fill='#fff' width={14} height={14} /></Avatar>}
				duration={900}
			>
				Проблема с отправкой данных в Storage
			</Snackbar>
			);
		}
	}

	return (
		<ConfigProvider>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout}>
						{/* {isDesktop &&						 */}
						<SplitCol
							// width={isDesktop ? contentWidth : '100%'}
							// maxWidth={isDesktop ? contentWidth : '100%'}
							width={300}
						>
							<View activePanel={activePanel}>
								<SplitCol
									// width={isDesktop ? contentWidth : '100%'}
									// maxWidth={isDesktop ? contentWidth : '100%'}
									width={300}
								>
									<Colomn id='col' go={go} />
								</SplitCol>
								<Home id={ROUTES.HOME} fetchedUser={fetchedUser} go={go} />
								<ChooseFriend id={ROUTES.CHOOSEFRIEND} go={go} activePanel={activePanel} />

							</View>
						</SplitCol>

						{/* } */}
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}

export default App;
