import bridge from '@vkontakte/vk-bridge';
import { useState, useEffect } from 'react';
import {
    Panel,
    PanelHeader,
    Header,
    Button,
    Group,
    Cell,
    Div,
    Avatar,
    HorizontalCell,
} from '@vkontakte/vkui';
import './ChooseFriend.css';
import dino from '../../img/dinoLove.svg';
import ProgressBar from '../progressBar/ProgressBar.js';

export function ChooseFriend(id, go) {
    const [fetchedFriend, setFriend] = useState([]);

    const [isPressed, setIsPressed] = useState(false);

    const [completed, setCompleted] = useState(0);
    const [disabled, setDisabled] = useState(false);
    // useEffect(() => {
    //     setInterval(() => setCompleted(Math.floor(Math.random() * 100) + 1), 2000);
    // }, []);


    const fetchFriends = () => {
        bridge
            .send('VKWebAppGetFriends')
            .then((data) => {
                if (data.users) {
                    console.log(data.users[0]);
                    setFriend(data.users[0]);
                }
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const loader = () => {
        setCompleted(0);
        setIsPressed(true);
        setDisabled(true)
        setCompleted(65);
    };
    // return <ProgressBar completed={67} />;

    const name = fetchedFriend.first_name;

    return (
        <Panel id={id}>
            <Div className='Container'>
                <ProgressBar completed={67} />

                <Div className='Button__position'>
                    <Button
                        className='Button'
                        onClick={fetchFriends}
                        data-to='home'
                        appearance={'positive'}
                        stretched={false}
                        size={'i '}
                        sizeY={'regular'}
                    >
                        выбрать друга
                    </Button>
                </Div>

                {name === null || name === undefined ? null : (
                    <>
                        <h4> {`насколько, ${name} любит котиков`}</h4>

                        <Div className='Person'>
                            <HorizontalCell size='s' header={name}>
                                <Avatar
                                    size={56}
                                    src={fetchedFriend.photo_200}
                                />
                            </HorizontalCell>
                        </Div>

                        <Div className='Button'>
                            <Button
                                className='Button__size'
                                onClick={loader}
                                appearance={'positive'}
                                size={'i'}
                                stretched={false}
                                disabled={disabled}
                            >
                                узнать
                            </Button>
                        </Div>
                        {isPressed ? <ProgressBar completed={completed} /> : null}
                    </>




                )}
            </Div>
        </Panel>
    );
}
